<button class="btn btn-primary mt-auto" onclick="fetch('{{ route('admin.order.accept') }}?order={{$order}}', {method: 'PATCH', headers: {'Accept': 'application/json', 'Content-Type': 'application/json', 'X-CSRF-TOKEN': '{{csrf_token()}}'}}).then(res => res.json()).then(res => {if (res.data.message) alert(res.data.message); else location.reload()})">{{$text}}</button>